import React from 'react'
import { connect } from 'react-redux'

import { withStyles } from '@material-ui/core/styles'
import { AppBar, Button, Toolbar, Avatar, Typography } from '@material-ui/core'
import CssBaseline from '@material-ui/core/CssBaseline'
import { createMuiTheme, MuiThemeProvider } from '@material-ui/core/styles'

import { Link } from 'react-router-dom'
import { GoogleLogin } from 'react-google-login'
import Routes from '..//Routes'
import ClientIds from '../config'
import axios from 'axios'

import { updateUser, updateColumns } from '../actions/simpleAction'

const theme = createMuiTheme({
  palette: {
    type: 'light'
  },
  typography: {
    useNextVariants: true
  },
  toolbar: {
    backgroundColor: 'white'
  }
})

const styles = theme => ({
  root: {
    flexGrow: 1
  },
  login: {
    right: 10,
    position: 'absolute'
  },
  logout: {
    right: 5,
    top: 13,
    position: 'absolute'
  },
  toolbar: {
    backgroundColor: '#008080'
  },
  save: {
    right: 90,
    top: 13,
    position: 'absolute'
  },
  signup: {
    right: 80,
    position: 'absolute'
  },
  avatar: {
    left: 35,
    top: 10,
    position: 'absolute'
  },
  userName: {
    left: 100,
    top: 16,
    position: 'absolute',
    text: theme.palette.secondary
  }
})

const saveBooks = (user, userState) => {
  const newUserState = {
    ...user,
    userState: userState
  }
  axios.post('http://localhost:5000/api/save', newUserState)
}

const login = (user, updateUser, updateColumns, userState) => {
  axios
    .get('http://localhost:5000/api/userState/${user.profileObj.email}')
    .then(function(response) {
      if (response.data) {
        updateColumns(response.data.userState.columns)
      }
      updateUser({ ...user.profileObj, isAuthenticated: true })
    })
    .catch(function(error) {
      const object = {
        ...user.profileObj,
        ...userState
      }

      axios.post('http://localhost:5000/api/userState', {
        object
      })
      console.log(error)
    })
}

const logout = (user, updateUser, updateColumns) => {
  updateUser({ ...user.profileObj, isAuthenticated: false })
}

const Navbar = ({ classes, userState, updateUser, updateColumns }) => {
  return (
    <div className={classes.root}>
      <MuiThemeProvider theme={theme}>
        <CssBaseline />
        <AppBar position="static">
          <Toolbar className={classes.toolbar}>
            {!userState.user.isAuthenticated && (
              <GoogleLogin
                onSuccess={user =>
                  login(user, updateUser, updateColumns, userState)
                }
                onFailure={console.log}
                clientId={ClientIds.GOOGLE_CLIENT_ID}
                render={renderProps => (
                  <Button
                    onClick={renderProps.onClick}
                    className={classes.login}
                    color="inherit"
                  >
                    Login with Google
                  </Button>
                )}
              />
            )}
            {userState.user.isAuthenticated && (
              <div>
                <Avatar
                  src={userState.user.imageUrl}
                  className={classes.avatar}
                  component={Link}
                  to="/profile"
                />
                <Typography
                  className={classes.userName}
                  variant="h6"
                  color="inherit"
                >
                  {userState.user.firstName
                    ? userState.user.firstName
                    : userState.user.givenName}
                </Typography>
                {
                  <Button
                    onClick={() => saveBooks(userState.user, userState)}
                    variant="h6"
                    color="inherit"
                    className={classes.save}
                  >
                    Save
                  </Button>
                }
                {
                  <Button
                    onClick={() =>
                      logout(userState.user, updateUser, updateColumns)
                    }
                    variant="h6"
                    color="inherit"
                    className={classes.logout}
                  >
                    Logout
                  </Button>
                }
              </div>
            )}
          </Toolbar>
        </AppBar>
        <Routes />
      </MuiThemeProvider>
    </div>
  )
}

function mapStateToProps(state) {
  return {
    userState: state.simpleReducer
  }
}

function mapDispatchToProps(dispatch) {
  return {
    updateUser: user => dispatch(updateUser(user)),
    updateColumns: state => dispatch(updateColumns(state))
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles)(Navbar))
