import {
  UPDATE_BOOKS,
  UPDATE_USER,
  UPDATE_COLUMNS,
  UPDATE_STATE
} from '../actions/simpleAction'

let initialState = {
  user: {
    isAuthenticated: false
  },
  books: {},
  columns: {
    'column-1': {
      id: 'column-1',
      title: 'To Read',
      bookIds: []
    },
    'column-2': {
      id: 'column-2',
      title: 'In Progress',
      bookIds: []
    },
    'column-3': {
      id: 'column-3',
      title: 'Done Reading',
      bookIds: []
    }
  },

  columnOrder: ['column-1', 'column-2', 'column-3']
}

export default (state = initialState, action) => {
  switch (action.type) {
    case UPDATE_BOOKS:
      return {
        ...state,
        books: action.books
      }
    case UPDATE_USER:
      return {
        ...state,
        user: action.user
      }
    case UPDATE_COLUMNS:
      return {
        ...state,
        columns: action.columns
      }
    case UPDATE_STATE:
      return action.state
    default:
      return state
  }
}
