import React, { Component } from 'react'
import styled from 'styled-components'
import { Draggable } from 'react-beautiful-dnd'

const Container = styled.div`
  border: 8px double #b2c2bf;
  border-radius: 10px;
  margin-bottom: 30px;
  width: 100%;
  background-color: ${props => (props.isDragging ? '#64e764' : '#eaece5')};
  display: flex;
  height: 400px;
`

const BookPicture = styled.div`
  width: 100px;
  height: 120px;
  margin-right: 8px;
  display: block;
`

const BookInfoLeft = styled.div`
  background-color: #ffffff;
  display: flex;
  flex-direction: column;
  padding: 20px;
  margin-right: 8px;
  width: 30%;
`
const BookInfoRight = styled.div`
  background-color: #ffffff;
  display: flex;
  flex-direction: column;
  width: 100%;
`

const Title = styled.h1`
  margin: 0 0 0 40px;
  padding-top: 10px;
  font-size: 25px;
  color: #474747;
  padding-right: 10px;
`

const Subtitle = styled.h2`
  padding-top: 10px;
  margin: 0 0 40px 40px;
  font-size: 15px;
  font-family: 'Helvetica', sans-serif;
  font-weight: 400;
  opacity: 0.6;
  letter-spacing: 0.05em;
  padding-right: 10px;
`

const Author = styled.h3`
  margin: 0 0 0 40px;
  font-family: 'Helvetica';
  color: #484f4f;
  line-height: 1.7em;
  font-size: 15px;
  overflow: hidden;
  border-radius: 20px;
`

const Categories = styled.h3`
  font-family: 'Helvetica';
  color: purple;
  line-height: 2em;
  font-size: 15px;
  font-weight: lighter;
`
const Metrics = styled.h3`
  font-family: 'Helvetica';
  color: #3b3a30;
  line-height: 2em;
  font-size: 15px;
  font-weight: lighter;
`

const Description = styled.p`
  margin: 0 0 0 40px;
  font-family: 'Helvetica', serif;
  color: #8d8d8d;
  line-height: 1.7em;
  font-size: 15px;
  font-weight: lighter;
  overflow: hidden;
  padding-right: 10px;
`

export default class Book extends Component {
  render() {
    var picture = this.props.book.thumbnail
    var description = this.props.book.description
    var authors = this.props.book.author
    var rating = this.props.book.averageRating
    var ratingsCount = this.props.book.ratingsCount
    var pageCount = this.props.book.pageCount
    var categories = this.props.book.categories
    var title = this.props.book.title

    console.log(this.props.book)
    return (
      <Draggable
        key={this.props.book.id}
        draggableId={this.props.book.id}
        index={this.props.index}
      >
        {(provided, snapshot) => (
          <Container
            {...provided.draggableProps}
            {...provided.dragHandleProps}
            ref={provided.innerRef}
            isDragging={snapshot.isDragging}
          >
            <BookInfoLeft>
              <BookPicture>
                <a href={this.props.book.infoLink}>
                  <img src={picture} width="100" height="120" />
                </a>
              </BookPicture>
              {categories.map(category => (
                <Categories> {category} </Categories>
              ))}
              <Metrics>
                <p>{rating}</p>
                <p>{ratingsCount}</p>
                <p>{pageCount}</p>
              </Metrics>
              <br />
            </BookInfoLeft>
            <BookInfoRight>
              <Title>{title}</Title>
              <Subtitle>{this.props.book.subtitle}</Subtitle>

              {authors.map(author => (
                <Author> {author} </Author>
              ))}
              <Description>{description}</Description>
            </BookInfoRight>
          </Container>
        )}
      </Draggable>
    )
  }
}
